sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	verifyDate: function(whenNeeded){
		var rightnow = new GlideDateTime();
	// Create a GlideDateTime object for the When needed date

	// If the When needed date is before rightnow, do not write the record to the database
	// Output an error message to the screen
	if(whenNeeded.before(rightnow)){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		return false;
	}

	// If the When needed date is same day request, do not write the record to the database
	// Output an error message to the screen
	var today = rightnow.getLocalDate();
	var istoday = whenNeeded.getLocalDate();
	// Compare today and istoday to see if they are the same day
	if(today.compareTo(istoday) == 0){
		gs.addErrorMessage("Same day requests are not allowed. Please select a date after today.");
		return false;
	}
		return true;
	},

    type: 'NeedItUtils'
};
